mapper.select:
SELECT id, nickname, email, password,
reg_date as regDate, last_access_date as lastAccessDate, user_grade as userGrade
FROM users
ORDER BY ${sortby} ${orderby}
LIMIT #{offset}, #{limit}

SELECT * FROM boards
ORDER BY ${sortby} ${orderby}
LIMIT #{offset}, #{limit}

SELECT * FROM notes
ORDER BY ${sortby} ${orderby}
LIMIT #{offset}, #{limit}

SELECT id, content, images, create_date as createDate,
last_modified_date as lastModifiedDate,
comment_id as parentCommentId, note_id as noteId, comment_author_id as commentAuthorId
FROM comments
ORDER BY ${sortby} ${orderby}
LIMIT #{offset}, #{limit}
