mapper.select:
SELECT * FROM notes
	WHERE board_id = #{boardPos}
	ORDER BY create_date DESC
	LIMIT #{offset}, #{pageSize};

  	SELECT n.*
		FROM notes n
			INNER JOIN (
				SELECT note_id
				FROM note_token
				WHERE token IN 

			        #{token}

			) nt ON n.id = nt.note_id
			WHERE n.board_id = #{board}
			GROUP BY n.id
			HAVING count(n.id) >= #{count};
